titles.css:
.title-01 {
text-align: center;
font-size: 2.8em;
color: var(--primary-color);
font-weight: normal;
}

.title-01 strong {
font-weight: bold;
color: var(--dark);
}

buttons.css:
.btn--mod1 {
position: relative;
}

.btn--mod1:hover {
background-color: var(--dark);
}

.btn--mod1:before,
.btn--mod1:after {
content: '';
position: absolute;
top: 0;
left: 0;
width: 100%;
height: 100%;
z-index: -1;
opacity: 1;
transition: all 1s ease;
}

.btn--mod1:before {
border-radius: 3px;
background-color: #ccc;
transform: translate(7px, 7px);
}

.btn--mod1:after {
border-radius: 3px;
background-color: #444;
transform: translate(-7px, -7px);
}

.btn--mod1:hover:before,
.btn--mod1:hover:after {
opacity: 0;
transform: translate(0)
}
